configuration.js:
$(document).ready(function() {
    $('img').click(function() {
        var src = $(this).attr('src');
        var modal = '<div id="imgModal" style="position:fixed;top:0;left:0;width:100%;height:100%;background:rgba(0,0,0,0.9);z-index:9999;display:flex;align-items:center;justify-content:center;" onclick="$(this).remove();">' +
                    '<img src="' + src + '" style="max-width:90%;max-height:90%;border-radius:10px;box-shadow:0 0 25px rgba(255,255,255,0.5);">' +
                    '</div>';
        $('body').append(modal);
    });
});
